const eventType = event.event.type.event.toUpperCase()
const granted = event.event.granted
const direction = event.event.direction.direction.toUpperCase()
const reason = event.event.reason.reason.toUpperCase()
const forceLock = event.inputs.forceLock
const fireAlarm = event.inputs.fireAlarm
const door = doors[event.event.door] || '???'

const item = {
    'timestamp': timestamp,
    'type': eventType,
    'granted': granted,
    'door': door,
    'direction': direction,
    'reason': reason,
    'forceLock': forceLock,
    'fireAlarm': fireAlarm
}

events.push(item)

if (events.length > 32) {
    events.shift()
}

context.set('events', events)

