switch (msg.topic) {
    case 'set-address':
        flow.set('set-address.address', msg.payload)
        return null;

    case 'set-subnet':
        flow.set('set-address.subnet', msg.payload)
        return null;

    case 'set-gateway':
        flow.set('set-address.gateway', msg.payload)
        return null;

    case 'set':
        let address = flow.get('set-address.address')
        let subnet = flow.get('set-address.subnet')
        let gateway = flow.get('set-address.gateway')

        if (!address) {
          address = '192.168.1.100'
        }

        if (!subnet) {
          subnet = '255.255.255.20'
        }

        if (!gateway) {
          gateway = '192.168.1.1'
        }

        msg.payload = {
           deviceId: deviceId,
           address: address,
           subnet: subnet,
           gateway: gateway
        }        

        return msg
}

const format = (n, l) => String(n).padStart(l, '0')
const deviceId = 405419896

switch (msg.topic) {
    case 'set-date':
        flow.set('set-time.date', new Date(msg.payload))
        return null;

    case 'set-time':
        const dt = new Date(msg.payload)
        const tz = dt.getTimezoneOffset()
        flow.set('set-time.time', new Date(msg.payload + tz*60*1000))
        return null;

    case 'set':
        let date = flow.get('set-time.date')
        let time = flow.get('set-time.time')

        if (!date) {
          date = new Date()
        }

        if (!time) {
          time = new Date()
        }

        const year = format(date.getFullYear(), 4)
        const month = format(date.getMonth()+1, 2)
        const day = format(date.getDate(), 2)
        const hours = format(time.getHours(), 2)
        const minutes = format(time.getMinutes(), 2)
        const seconds = format(time.getSeconds(), 2)

        msg.payload = { 
            deviceId: deviceId,
            datetime: year + '-' + month + '-' + day + ' ' + hours + ':' + minutes + ':' + seconds
        }

        return msg
}

