    const re = /^([0-9]{4})-([0-9]{2})-([0-9]{2})$/
    const match = re.exec(s)

    if (match !== null && match.length > 3) {
        const year = parseInt(match[1], 10)
        const month = parseInt(match[2], 10)
        const day = parseInt(match[3], 10)
        const date = new Date()

        date.setYear(year)
        date.setMonth(month - 1)
        date.setDate(day)

        return date
    }

    return null
}

